mapper.select:
SELECT A.id, A.workspace_id, C.workspace_name, B.user_id as ownerId, B.nick, B.color, A.subject, A.content, A.start_date, A.endDate 
FROM CALENDAR A LEFT OUTER JOIN USER_WORKSPACE ON A.owner_user_workspace_id = B.id JOIN WORKSPACE C ON A.workspace_id = C.id 
WHERE A.id = #{calendarId}

SELECT A.id, A.workspace_id, C.workspace_name, B.user_id as ownerId, B.nick, B.color, A.subject, A.content, A.start_date, A.endDate 
FROM CALENDAR A LEFT OUTER JOIN USER_WORKSPACE ON A.owner_user_workspace_id = B.id JOIN WORKSPACE C ON A.workspace_id = C.id 
WHERE A.workspace_id = #{workspaceId} AND A.start_date < #{endDate} AND A.end_date >= #{startDate}

SELECT A.id, A.workspace_id, C.workspace_name, B.user_id as ownerId, B.nick, B.color, A.subject, A.content, A.start_date, A.endDate 
FROM CALENDAR A LEFT OUTER JOIN USER_WORKSPACE ON A.owner_user_workspace_id = B.id JOIN WORKSPACE C ON A.workspace_id = C.id 
WHERE A.workspace_id = #{workspaceId} AND A.start_date < #{endDate} AND A.end_date >= #{startDate}
